(function executeRule(current, previous /*null when async*/) {
    function getDeptNameBySysId(userSysId) {
        var userGR = new GlideRecord('sys_user');
        if (userGR.get(userSysId)) {
            var deptID = userGR.getValue('department');
            if (deptID) {
                var deptGR = new GlideRecord('cmn_department');
                if (deptGR.get(deptID)) {
                    return deptGR.getValue('name');
                }
            }
        }
        return '';
    }

    function getDeptNameByUserName(userName) {
        var userGR = new GlideRecord('sys_user');
        userGR.addQuery('user_name', userName);
        userGR.query();
        if (userGR.next()) {
            var deptID = userGR.getValue('department');
            if (deptID) {
                var deptGR = new GlideRecord('cmn_department');
                if (deptGR.get(deptID)) {
                    return deptGR.getValue('name');
                }
            }
        }
        return '';
    }

    current.u_dept_assignee = current.u_assignee ? getDeptNameBySysId(current.u_assignee) : '';
    current.u_dept_user = current.sys_created_by ? getDeptNameByUserName(current.sys_created_by) : '';
})(current, previous);
