(function executeRule(current) {

    var refSysId = current.u_created_date_from_user_master;
    if (refSysId) {
        var gr = new GlideRecord('x_1130583_custom_a_user_master_document');
        if (gr.get(refSysId)) {
            current.u_created_date_from_user_master = gr.sys_created_on; // Assign the value
        }
    }

})(current);
