sys_script_client.script:
function onChange(newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    // When a user is selected, fetch their employee number and department details
    var ga = new GlideAjax('UserDepartmentInfo');
    ga.addParam('sysparm_name', 'getUserDetails');
    ga.addParam('sysparm_user_id', newValue);
    ga.getXMLAnswer(function(response) {
        var answer = response;
        var data = JSON.parse(answer);

        // Populate the fields with the returned data
        g_form.setValue('employee_number', data.employee_number || '');
        g_form.setValue('department_name', data.department_name || '');
        g_form.setValue('department_id', data.department_id || '');
    });
}
