sys_script_include.script:
var UserDepartmentInfo = Class.create();
UserDepartmentInfo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getUserDetails: function() {
        var userID = this.getParameter('sysparm_user_id');
        var result = {
            employee_number: '',
            department_name: '',
            department_id: ''
        };

        var userGR = new GlideRecord('sys_user');
        if (userGR.get(userID)) {
            result.employee_number = userGR.getValue('employee_number');

            var deptID = userGR.getValue('department');
            if (deptID) {
                var deptGR = new GlideRecord('cmn_department');
                if (deptGR.get(deptID)) {
                    result.department_name = deptGR.getValue('name');
                    result.department_id = deptGR.getValue('id');
                }
            }
        }

        return JSON.stringify(result);
    },

    type: 'UserDepartmentInfo'
});
