sys_script_include.script:
var TempSaveAjax = Class.create();
TempSaveAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
   saveTempData: function () {
       var uid = gs.getUserID();
       var data = {
           name: this.getParameter('sysparm_name_val') || '',
           employee_number: this.getParameter('sysparm_employee_number') || '',
           department_name: this.getParameter('sysparm_department_name') || '',
           department_code: this.getParameter('sysparm_department_code') || ''
       };
       gs.getUser().savePreference('temp_form_data_' + uid, JSON.stringify(data));
       return 'saved';
   },
   getTempData: function () {
       var uid = gs.getUserID();
       var data = gs.getUser().getPreference('temp_form_data_' + uid);
       return data || '';
   },
   type: 'TempSaveAjax'
});
