sys_script_include.script:
// Name: GetCurrentUserInfo
// Client Callable: âœ… Checked
// Accessible from: All application scopes (if needed globally)

var GetCurrentUserInfo = Class.create();
GetCurrentUserInfo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    /**
     * This method returns the current user's sys_id and user_name.
     * It is called from client-side using GlideAjax.
     */
    getUserDetails: function() {
        var userSysId = gs.getUserID(); // sys_id of the logged-in user
        var userGR = new GlideRecord('sys_user');

        if (userGR.get(userSysId)) {
            var result = {
                sys_id: userGR.getValue('sys_id'),
                user_name: userGR.getValue('user_name')
            };
            return JSON.stringify(result); // Return as JSON string for client-side parsing
        }

        return JSON.stringify({}); // Return empty object if user not found
    },

    type: 'GetCurrentUserInfo'
});
