sys_ui_policy.script_true:
function onCondition() {
	var type = g_form.getValue('category');
	alert(type);

	if(type == "option_a"){
		g_form.setVisible('name', true);
		g_form.setVisible('approval_state', false);
	} else if(type == "option_b"){
		g_form.setVisible('name', false);
		g_form.setVisible('approval_state', true);
	} else{
		g_form.setVisible('name', false);
		g_form.setVisible('approval_state', false);
	}
}
